// AHADITH
const AHADITH=[
  {title:"فضل الذكر",text:"مَثَلُ الَّذِي يَذْكُرُ رَبَّهُ وَالَّذِي لَا يَذْكُرُ رَبَّهُ مَثَلُ الْحَيِّ وَالْمَيِّتِ.",src:"رواه البخاري"},
  {title:"أفضل الصيام",text:"أَفْضَلُ الصِّيَامِ بَعْدَ رَمَضَانَ شَهْرُ اللَّهِ الْمُحَرَّمُ، وَأَفْضَلُ الصَّلَاةِ بَعْدَ الْفَرِيضَةِ صَلَاةُ اللَّيْلِ.",src:"رواه مسلم"},
  {title:"الدعاء في رمضان",text:"ثَلَاثَةٌ لَا تُرَدُّ دَعْوَتُهُمْ: الصَّائِمُ حَتَّى يُفْطِرَ، وَالْإِمَامُ الْعَادِلُ، وَدَعْوَةُ الْمَظْلُومِ.",src:"رواه الترمذي"},
  {title:"فضل رمضان",text:"مَنْ صَامَ رَمَضَانَ إِيمَانًا وَاحْتِسَابًا غُفِرَ لَهُ مَا تَقَدَّمَ مِنْ ذَنْبِهِ.",src:"رواه البخاري ومسلم"},
  {title:"فضل التلاوة",text:"اقْرَءُوا الْقُرْآنَ فَإِنَّهُ يَأْتِي يَوْمَ الْقِيَامَةِ شَفِيعًا لِأَصْحَابِهِ.",src:"رواه مسلم"},
  {title:"ليلة القدر",text:"مَنْ قَامَ لَيْلَةَ الْقَدْرِ إِيمَانًا وَاحْتِسَابًا غُفِرَ لَهُ مَا تَقَدَّمَ مِنْ ذَنْبِهِ.",src:"رواه البخاري ومسلم"},
  {title:"خير الناس",text:"خَيْرُكُمْ مَنْ تَعَلَّمَ الْقُرْآنَ وَعَلَّمَهُ.",src:"رواه البخاري"},
  {title:"الصدقة",text:"الصَّدَقَةُ تُطْفِئُ الْخَطِيئَةَ كَمَا يُطْفِئُ الْمَاءُ النَّارَ.",src:"رواه الترمذي"},
  {title:"الصلاة على النبي",text:"مَنْ صَلَّى عَلَيَّ صَلَاةً وَاحِدَةً صَلَّى اللَّهُ عَلَيْهِ عَشْرًا.",src:"رواه مسلم"},
  {title:"بر الوالدين",text:"رِضَا الرَّبِّ فِي رِضَا الْوَالِدَيْنِ، وَسَخَطُ الرَّبِّ فِي سَخَطِ الْوَالِدَيْنِ.",src:"رواه الترمذي"},
  {title:"حسن الخلق",text:"أَكْمَلُ الْمُؤْمِنِينَ إِيمَانًا أَحْسَنُهُمْ خُلُقًا، وَخِيَارُكُمْ خِيَارُكُمْ لِنِسَائِهِمْ.",src:"رواه الترمذي"},
  {title:"التوبة",text:"التَّائِبُ مِنَ الذَّنْبِ كَمَنْ لَا ذَنْبَ لَهُ.",src:"رواه ابن ماجه"},
  {title:"الإخلاص",text:"إِنَّ اللَّهَ لَا يَنْظُرُ إِلَى صُوَرِكُمْ وَأَمْوَالِكُمْ، وَلَكِنْ يَنْظُرُ إِلَى قُلُوبِكُمْ وَأَعْمَالِكُمْ.",src:"رواه مسلم"},
  {title:"فضل قيام الليل",text:"عَلَيْكُمْ بِقِيَامِ اللَّيْلِ فَإِنَّهُ دَأَبُ الصَّالِحِينَ قَبْلَكُمْ.",src:"رواه الترمذي"},
  {title:"الدعاء عند الإفطار",text:"ذَهَبَ الظَّمَأُ وَابْتَلَّتِ الْعُرُوقُ وَثَبَتَ الْأَجْرُ إِنْ شَاءَ اللَّهُ.",src:"رواه أبو داود"},
  {title:"السحور",text:"تَسَحَّرُوا فَإِنَّ فِي السُّحُورِ بَرَكَةً.",src:"رواه البخاري ومسلم"},
  {title:"الأمانة",text:"لَا إِيمَانَ لِمَنْ لَا أَمَانَةَ لَهُ، وَلَا دِينَ لِمَنْ لَا عَهْدَ لَهُ.",src:"رواه أحمد"},
  {title:"حق المسلم",text:"الْمُسْلِمُ مَنْ سَلِمَ الْمُسْلِمُونَ مِنْ لِسَانِهِ وَيَدِهِ.",src:"رواه البخاري"},
  {title:"الكلمة الطيبة",text:"الْكَلِمَةُ الطَّيِّبَةُ صَدَقَةٌ.",src:"رواه البخاري"},
  {title:"الرحمة",text:"الرَّاحِمُونَ يَرْحَمُهُمُ الرَّحْمَنُ، ارْحَمُوا مَنْ فِي الْأَرْضِ يَرْحَمْكُمْ مَنْ فِي السَّمَاءِ.",src:"رواه أبو داود"},
  {title:"الصبر",text:"مَا أُعْطِيَ أَحَدٌ عَطَاءً خَيْرًا وَأَوْسَعَ مِنَ الصَّبْرِ.",src:"رواه البخاري ومسلم"},
  {title:"العلم",text:"مَنْ سَلَكَ طَرِيقًا يَلْتَمِسُ فِيهِ عِلْمًا سَهَّلَ اللَّهُ لَهُ بِهِ طَرِيقًا إِلَى الْجَنَّةِ.",src:"رواه مسلم"},
  {title:"الاستغفار",text:"مَنْ لَزِمَ الِاسْتِغْفَارَ جَعَلَ اللَّهُ لَهُ مِنْ كُلِّ ضِيقٍ مَخْرَجًا، وَمِنْ كُلِّ هَمٍّ فَرَجًا.",src:"رواه أبو داود"},
];

// HOME DHIKR CARDS (inline rotating)
const HOME_ADHKAR=(()=>{
  const all=[];
  // من كل فئة
  Object.entries(ADHKAR).forEach(([cat,items])=>{
    const label={"morning":"أذكار الصباح","evening":"أذكار المساء","sleep":"أذكار النوم","after_prayer":"أذكار ما بعد الصلاة","quran_adhkar":"أدعية قرآنية","dua_general":"أدعية عامة","istighfar":"الاستغفار","tasbih":"التسبيح"}[cat]||cat;
    items.forEach(i=>all.push({text:i.text,src:i.note||"",cat:label}));
  });
  // أحاديث
  HADITHS.forEach(h=>all.push({text:h.text,src:h.src,cat:"حديث نبوي شريف"}));
  return all;
})();

